g.script:
//
const script = document.getElementById("s");
const template = script.previousElementSibling;

const rot = [0, 0x176, 0x2EC, 0x39A, 0x4FB, 0x58D];
const x = [
 2, 0, 1,-1,
 4, 5, 3, 4,
 0, 1, 2, 3
];
const y = [
 3, 3, 0, 0,
-3, 0, 0, 3,
-3,-6,-3,-6
];

for(var id1 = 0; id1 < 6; ++id1)
{
	for(var id0 = 0; id0 < 16; ++id0)
	{
		var marker = template.cloneNode(true);
		const bin = (id0 * 0x111) ^ rot[id1];	//binary bit pattern
		if(bin)
		{
			marker.setAttribute("transform", "translate(" + (7 * id0) + ' ' + (21 * (2 * id1 + (id0 & 1))) + ')');
		}
		template.before(marker);
		marker = marker.children[2];

		for(var i = 0; i < 12; ++i)
		{
			const bit = document.createElementNS("http://www.w3.org/2000/svg", "use");
			const bool = (bin >> i) & 1;

			bit.setAttribute("href", '#' + bool);
			if(x[i])	// only set if it's non-zero
				bit.setAttribute("x", x[i].toString());
			if(y[i])	// only set if it's non-zero
				bit.setAttribute("y", y[i].toString());

			if(bool)
				marker.after(bit);
			else
				marker.before(bit);
		}
		marker.remove();
	}
}

template.remove();
script.remove();
//
